mapper.select:
SELECT
    gift.*, usr.channel_name, usr.company_name
FROM sn_channel_gift AS gift
LEFT JOIN `user_channel_info` AS usr
ON gift.channel_id = usr.channel_id

    gift.is_deleted = 0

        and gift.channel_id = #{searchReqVO.channelId}


        and gift.channel_name = #{searchReqVO.channelName}


        and gift.company_name = #{searchReqVO.companyName}


        and gift.channel_pwd = #{searchReqVO.channelPwd}


        and gift.gift_name like concat('%',#{searchReqVO.giftName},'%')


        and gift.status = #{searchReqVO.status}


ORDER BY gift.create_time DESC

mapper.update:
UPDATE sn_channel_gift
SET remain_count = remain_count + #{count}
WHERE id = #{id}
  AND is_deleted = 0
  AND remain_count + #{count} >= 0
