mapper.select:
select

from t_sub_account


        and accountId = #{accountId,jdbcType=VARCHAR}


        and status = #{status,jdbcType=VARCHAR}


        and subAccountType = #{subAccountType,jdbcType=VARCHAR}


        and feeRoleId = #{feeRoleId,jdbcType=INTEGER}

mapper.insert:
insert into t_sub_account (
    subAccountId, accountId, status,
    subAccountType, createDate, feeRoleId,
    feeRateBeginDate, feeRateEndDate
)
values (
           #{subAccountId,jdbcType=VARCHAR}, #{accountId,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},
           #{subAccountType,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{feeRoleId,jdbcType=INTEGER},
           #{feeRateBeginDate,jdbcType=TIMESTAMP}, #{feeRateEndDate,jdbcType=TIMESTAMP}
       )

mapper.update:
update t_sub_account
accountId = #{accountId,jdbcType=VARCHAR}, status = #{status,jdbcType=VARCHAR}, subAccountType = #{subAccountType,jdbcType=VARCHAR}, createDate = #{createDate,jdbcType=TIMESTAMP}, feeRoleId = #{feeRoleId,jdbcType=INTEGER}, feeRateBeginDate = #{feeRateBeginDate,jdbcType=TIMESTAMP}, feeRateEndDate = #{feeRateEndDate,jdbcType=TIMESTAMP},
where subAccountId = #{subAccountId,jdbcType=VARCHAR}
